import {React, useState} from "react";
import validator from 'validator';

const Register = (props) => {

    const [username, setUsername] = useState('')
    const [email, setEmail] = useState('')
    const [password, setPassword] = useState('')
    const [formErrors, setFormErrors] = useState({})

    const errors = {}

    const runValidations = () => {
        if(username.trim().length === 0){
            errors.username = 'Name cannot be blank'
        }
        if(email.trim().length === 0){
            errors.email = 'Email cannot be blank'
        } else if (!validator.isEmail(email)) {
            errors.email = "Invalid email format"
        }
        if(password.trim().length === 0){
            errors.password = 'Password cannot be blank'
        }
    }

    const handleSubmit = (e) => {

        e.preventDefault()

        runValidations()

        if (Object.keys(errors).length === 0) {
            setFormErrors({})
            const formData = {
                username: username,
                email: email,
                password: password
            }
            console.log(formData)
        } else {
            setFormErrors(errors)
        }
    }

    const handleChange = (e) => {

        if (e.target.name === "username"){
            setUsername(e.target.value)
        } else if (e.target.name === "email") {
            setEmail(e.target.value)
        } else if (e.target.name === "password") {
            setPassword(e.target.value)
        }
    }

    return (
        <div>
            <h2>Register with Us</h2>
            <form onSubmit={handleSubmit}>
                <input type="text" 
                        name="username" 
                        value={username} 
                        onChange={handleChange} 
                        placeholder="Enter Username"
                        /> <br/>
                        {formErrors.username && <span>{formErrors.username}</span>} <br/>

                <input type="text" name="email" value={email} onChange={handleChange} placeholder="Enter Email"/> <br/>
                {formErrors.email && <p>{formErrors.email}</p>}
                <input type="text" name="password" value={password} onChange={handleChange} placeholder="Enter Password"/> <br/>
                {formErrors.password && <p>{formErrors.password}</p>}
                <input type="submit"/>
            </form>
        </div>
    )
}

export default Register
