import {React, useState, useEffect} from "react";
import NoteForm from './NoteForm'
import NotesList from './NotesList'
import axios from "axios";

const NoteBook = (props) => {

    const [title, setTitle] = useState('')
    const [body, setBody] = useState('')
    const [id, setId] = useState('')
    const [notesList, setNotesList] = useState([])

    useEffect(()=>{
        axios.get('http://dct-user-auth.herokuapp.com/api/notes', {
            headers: {
                'x-auth': localStorage.getItem('token')
            }
        })
        .then((response)=>{
            console.log(response.data)
            const result = response.data
            setNotesList(result)
            setId(result._id)
        })
        .catch((err)=>{
            console.log(err)
        })
    },[title,body])

    const handleChange = (e) => {
        if (e.target.name === "title"){
            setTitle(e.target.value)
        } else if (e.target.name === "body") {
            setBody(e.target.value)
        }
    }

    const handleSubmit = (e) => {
        e.preventDefault()
        const formData = {
            title: title,
            body:body
        }
        console.log(formData)
        axios.post('http://dct-user-auth.herokuapp.com/api/notes', formData, {
            headers: {
                'x-auth': localStorage.getItem('token')
            }
        })
        .then((response)=>{
            console.log(response.data)
        })
        .catch((err)=>{
            alert(err)
        })
    }

    const removeItem = (id) => {
        const result = notesList.filter(note=>{
            return note._id !== id
        })
        setNotesList(result)
    }

    return (
        <div>
            <h1>Signed In - My Notes</h1>
            <NoteForm title={title} body={body} handleChange={handleChange} handleSubmit={handleSubmit}/>
            <NotesList notesList={notesList} removeItem={removeItem} id={id}/>
        </div>
    )
}

export default NoteBook
