import {React} from 'react'
import axios from 'axios'

const NotesItem = (props) => {

    const {noteTitle, noteBody, noteId, removeItem} = props

    const handleDelete = () => {
        const confirmRemove = window.confirm('Are you sure?')
        if(confirmRemove){
            axios.delete(`http://dct-user-auth.herokuapp.com/api/notes/${noteId}`,{
                headers: {
                    'x-auth': localStorage.getItem('token')
                }
            })
            .then((response)=>{
                const result = response.data
                console.log(result)
                removeItem(result._id)
            })
            .catch((err)=>{
                alert(err.message)
            })
        }
    }


    const handleClick = () => {
        axios.get(`http://dct-user-auth.herokuapp.com/api/notes/${noteId}`,{
            headers: {
                'x-auth': localStorage.getItem('token')
            }
        })
        .then((response)=>{
            const result = response.data
            window.alert(`${result.title} -
            ${result.body}`)
        })
        .catch((err)=>{
            alert(err.message)
        })
    }

    return (
        <div>
            <h2 onClick={handleClick}>{noteTitle}</h2>
            <p>{noteBody}</p>
            <button onClick={handleDelete}>Delete</button>
            <hr/>
        </div>
    )
}

export default NotesItem
